INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(124128, 'zhCN', '', '向你致敬，勇士。', 26365),
(108026, 'zhCN', '很高兴见到你，朋友。', '', 26365),
(123323, 'zhCN', '你真走运！魔法商业区刚刚开了一家叫作“往日纪念”的新商店。', '你真走运！魔法商业区刚刚开了一家叫作“往日纪念”的新商店。', 26365),
(123318, 'zhCN', '克拉苏斯平台在达拉然的最东边。$B$B城市的飞行管理员就在那里。', '克拉苏斯平台在达拉然的最东边。$B$B城市的飞行管理员就在那里。', 26365),
(123317, 'zhCN', '你可以前往达拉然东北部的部落区，去风行者圣殿的“肮脏的野兽”瞧一瞧。$B$B听说那里非常脏乱，但是有一位很棒的烹饪训练师。', '你可以前往达拉然东北部的部落区，去风行者圣殿的“肮脏的野兽”瞧一瞧。$B$B听说那里非常脏乱，但是有一位很棒的烹饪训练师。', 26365),
(123316, 'zhCN', '英雄之家位于达拉然西南部的灰牙领地旁边。$B$B那里不仅设有露天啤酒店，还汇集了全城最好的烹饪训练师。', '英雄之家位于达拉然西南部的灰牙领地旁边。$B$B那里不仅设有露天啤酒店，还汇集了全城最好的烹饪训练师。', 26365),
(125465, 'zhCN', '织幻者图维斯就在简单附魔里做生意。', '织幻者图维斯就在简单附魔里做生意。', 26365);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(119956, 'zhCN', '$c，这是什么？', '', 26365),
(104100, 'zhCN', '你们准备好面对我了吗？', '', 26365),
(100200, 'zhCN', '按照传统，它已经属于胜利者了。斯科瓦尔德，你的抗议来得太迟了。', '', 26365),
(102920, 'zhCN', '如果这些所谓的“勇士”不肯放弃圣盾……那就让他们去死吧！', '', 26365),
(102919, 'zhCN', '不！我也证明了自己，奥丁。我是神王斯科瓦尔德！这些凡人休想抢走我的圣盾！', '', 26365),
(111071, 'zhCN', '谢谢你，凡人。厄诺克再也不能来此偷猎了。', '', 26365),
(104624, 'zhCN', '你们获得了我的祝福，勇士！希望能在荣耀大厅中见到你们！', '', 26365),
(104620, 'zhCN', '强大的芬雷尔在此出没。只有足智多谋的人才能杀掉这头野兽——多么完美的考验。', '', 26365),
(102967, 'zhCN', '', '我不紧张。就是……有点激动。我一生都在渴望这个荣耀的时刻。', 26365),
(102971, 'zhCN', '', '我在风暴峡湾见过他们。他们的行为既不荣耀，也不道德。', 26365),
(102973, 'zhCN', '', '我要亲自把他们赶出这座圣殿！', 26365),
(102968, 'zhCN', '这才像真正的瓦格里。', '', 26365),
(102966, 'zhCN', '你紧张吗，赫娅？这可以理解——', '', 26365),
(102965, 'zhCN', '', '凭借一系列英勇事迹，赫娅赢得了这个神圣的仪式。她将晋升为我麾下的精英瓦格里，她的名字将永远回荡在这座金色的殿堂。', 26365),
(102974, 'zhCN', '', '仪式还没有完成。去解决那些入侵者。', 26365),
(102969, 'zhCN', '凡人闯入者来了！', '', 26365),
(104616, 'zhCN', '在开始下一个试炼前，你们要击败赫娅和芬雷尔，以证明你们的实力！', '', 26365);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(130642, 'zhCN', '', '我从没相信过背叛者或他的伊利达雷走狗，但为了大局，我愿意先搁置疑虑。\n\n可一旦燃烧军团倒下，我们还要那些被他们污染过的家伙干嘛？\n\n', 25996),
(125478, 'zhCN', '我们在与燃烧军团的战斗中已经付出了惨重的代价。许多勇敢的英雄在这场战役中牺牲。我们决不能让他们的牺牲白费。\n\n让我们把握住胜利的机会吧！', '', 25996),
(112352, 'zhCN', '', '我们银行的安保措施一流，你哪怕是想拿回自己的东西也会遇到麻烦的！哈哈！', 25996),
(103914, 'zhCN', '我们的单向礼物发放数量已达上限。该上限每日重置一次。', '', 25996),
(116388, 'zhCN', '哟，伙计！我有消息要告诉你！', '', 25996),
(110017, 'zhCN', '所有能拿得动锤子和钳子的蠢货都能打铁，但只有大师级的工匠才能将奥术的精华融入他的作品中，锻造出兼具灵巧和威猛的武器。', '', 25996),
(125884, 'zhCN', '我可以送你回以太大厅。你想回去吗？', '我可以送你回以太大厅。你想回去吗？', 25996),
(112062, 'zhCN', '', '看来是暮色卫队在维持聚会的秩序。我建议你还是偷偷潜入吧。', 25996),
(112075, 'zhCN', '', '你还要沿途关闭他们的奥术道标，防止哨兵敲响警钟。', 25996),
(103023, 'zhCN', '你将与整座至高岭为敌！', '', 25996),
(102710, 'zhCN', '粉身碎骨！', '', 25996),
(105698, 'zhCN', '看呐看呐……这下头还真是一团糟啊。我们上次来净化这些墓穴都是好久之前了。看来这里好像闹老鼠了啊。', '', 25996),
(123272, 'zhCN', '别再骚扰主人的客人了！', '', 25996),
(123427, 'zhCN', '主人已经不对劲很久了。但愿一场宴会能让他打起精神来！', '', 25996),
(111412, 'zhCN', '这是我们最黑暗的时刻。', '', 25996),
(129353, 'zhCN', '', '但创世之柱无法带来胜利。只有兼具力量与勇气的联军才能战胜燃烧军团。等你们做好决战的准备，我的残影就会回来帮助你们。艾泽拉斯的命运就掌握就在你们手中。', 25996),
(129348, 'zhCN', '', '不会吧？守护者！', 25996),
(129354, 'zhCN', '', '我们得去告诉卡德加。', 25996),
(129351, 'zhCN', '', '但还有希望。只要将创世之柱放进下方的大厅，你们就能修复我的结界，逼退入侵者。然后，你们得深入墓穴，用阿曼苏尔之眼镇压邪能风暴，并斩断军团与艾泽拉斯的联系。', 25996),
(129350, 'zhCN', '', '我是提瑞斯法守护者，艾格文。我以这个残影作为保护，以防止我在陵墓这里设下的结界失效。没错，我感觉军团之主正在密谋着…夺取他的战利品。', 25996),
(129349, 'zhCN', '', '多年前，圣盾保护了新生的世界之魂免遭疯狂的腐蚀。今天，它又保护了艾泽拉斯的勇士们。', 25996),
(129355, 'zhCN', '看来准备得很顺利。', '', 25996),
(129347, 'zhCN', '这是什么？', '', 25996),
(129337, 'zhCN', '我们失败了。军团将我们逼出了墓穴。你们是保护圣盾的最后壁垒了。他们来了……', '', 25996),
(129338, 'zhCN', '让他们来吧。我和玛维来堵住阶梯。', '', 25996),
(129340, 'zhCN', '', '伊利丹，要是我的战刃刺穿你的心脏，你可别见怪。这里的恶魔太多了，我很难认出你来。', 25996),
(127936, 'zhCN', '', '你以为你逃得了吗，伊利丹？再跑的话，我就用链子把你拴起来。', 25996),
(127937, 'zhCN', '尽管装腔作势吧，玛维。但现在，请你尽力跟上我。', '', 25996),
(128820, 'zhCN', '小笨蛋也想跟军团耍花招？再想想吧！', '', 25996),
(128697, 'zhCN', '我来找你了！', '', 25996),
(127930, 'zhCN', '啊，贵客终于到了。我要拔光你脑袋上的角，背叛者！', '', 25996),
(129326, 'zhCN', '', '我们的部队正在地面与军团交战。必须在我们元气大伤前将圣盾放到塔楼里。', 25996),
(127929, 'zhCN', '你逃不出我的手心，恶魔！', '', 25996),
(127931, 'zhCN', '', '伊利丹！见鬼，这太鲁莽了！', 25996),
(98942, 'zhCN', '这张桌子上列出了当前所有你可接的任务。', '这张桌子上列出了当前所有你可接的任务。', 25996),
(113341, 'zhCN', '', '我们今晚恐怕对付不了大魔导师了，但她迟早会受到制裁的。我们一定能想出办法来解决问题……', 25996),
(112329, 'zhCN', '', '太好了！有了钥匙，我们就能打开通往阳台的路。艾利桑德的死期到了！', 25996),
(111879, 'zhCN', '这个聚会真不错，不是吗？', '这个聚会真不错，不是吗？', 25996),
(111878, 'zhCN', '愿苏拉玛长盛不衰，朋友。', '愿苏拉玛长盛不衰，朋友。', 25996),
(111880, 'zhCN', '今天的乐师很不错啊，不是吗？', '今天的乐师很不错啊，不是吗？', 25996),
(111746, 'zhCN', '上半夜的时候，我正巧瞥见那个密探穿着长袖衣服。', '上半夜的时候，我正巧瞥见那个密探穿着长袖衣服。', 25996),
(111748, 'zhCN', '有人说，那个密探今晚穿了一件长袖的衣服。', '有人说，那个密探今晚穿了一件长袖的衣服。', 25996),
(111692, 'zhCN', '艾利桑德邀请来参加聚会的那个人？这可不是我说的啊，但那家伙真的是燃烧军团的密探。', '艾利桑德邀请来参加聚会的那个人？这可不是我说的啊，但那家伙真的是燃烧军团的密探。', 25996),
(111693, 'zhCN', '有人说，有个燃烧军团的成员匿名参加了这次聚会……', '有人说，有个燃烧军团的成员匿名参加了这次聚会……', 25996),
(111690, 'zhCN', '今晚的聚会好像有几张新面孔啊……', '今晚的聚会好像有几张新面孔啊……', 25996),
(111696, 'zhCN', '小心点。据说燃烧军团派了一个密探潜入了这次聚会。', '小心点。据说燃烧军团派了一个密探潜入了这次聚会。', 25996),
(111997, 'zhCN', '', '去跟那些多嘴的贵族聊聊，找到这个密探。可别乱说话引起别人的注意！', 25996),
(111996, 'zhCN', '', '情报表明，有个燃烧军团的密探参加了这个聚会。', 25996),
(111837, 'zhCN', '', '这应该能骗过那些语无伦次的醉酒客人，可明眼人一定能看穿它。一旦幻象消失，守卫就会扑上来的。', 25996),
(114467, 'zhCN', '就看你的了！', '', 25996),
(111989, 'zhCN', '', '探索这片区域，设法分化议会成员。只要他们落单，就不是你的对手了！', 25996),
(111988, 'zhCN', '', '小心点。我听说有个能够抽取彼此力量的恶魔议会。我们得切断他们的联系。', 25996),
(108580, 'zhCN', '这盏灯似乎经过了改装。它肯定就是让你用来发信号的东西了。', '这盏灯似乎经过了改装。它肯定就是让你用来发信号的东西了。', 25996),
(114466, 'zhCN', '情报称，军团将一条名叫威利玛的恶魔看门狗带进了城市。这倒方便了！我可以用它的眼睛强化网络。', '', 25996),
(103075, 'zhCN', '圣锤从来都没有选中过你，达古尔。它需要一位真正强大的主人，而你还不够格。', '', 25996),
(103076, 'zhCN', '成功了，英雄们。在杀了达古尔并夺回圣锤后，卓格巴尔已经崩溃了。虽然距离统一诸族还早，但是至高岭已经恢复了平衡。', '', 25996),
(103077, 'zhCN', '疯王达古尔的势力已经崩溃了，至高岭要感谢你们，英雄们。现在，我得去处理一些家事了。再见，希望我们下次能在比较平和的情况下重逢。', '', 25996),
(103061, 'zhCN', '群山将吞噬你们的骨头！', '', 25996),
(103059, 'zhCN', '抱歉，英雄们，我得走了。但请你们相信，我很为你们的事迹骄傲。我相信在这最后关头，你们不会让至高岭失望。', '', 25996),
(103058, 'zhCN', '让他们来吧！来看看违抗掌握了泰坦之力的真王是什么下场吧！', '', 25996),
(103057, 'zhCN', '那个牛头人认为你是天定的圣锤之主？真可悲。', '', 25996),
(103054, 'zhCN', '没有时间了！我们得立刻攻击达古尔！', '', 25996),
(103055, 'zhCN', '我很抱歉，英雄们。看来你们要找的宝藏是在这头野兽的身后。', '', 25996),
(103048, 'zhCN', '对！啊哈哈哈！等等——不！啊啊啊！', '', 25996),
(103043, 'zhCN', '万虫之母！站起来！', '', 25996),
(103047, 'zhCN', '她来了！纳拉萨斯会吃掉你们的！', '', 25996),
(103046, 'zhCN', '如果让他们完成召唤仪式，我们就无法靠近达古尔了！', '', 25996),
(103025, 'zhCN', '至高岭是的你葬身之地！', '', 25996),
(103026, 'zhCN', '尽管跑吧。这改变不了什么。', '', 25996),
(103011, 'zhCN', '求求你，你已经把食物都抢光了。', '', 25996),
(103012, 'zhCN', '快搜！贱民把粮食都藏了起来，不愿献给部队！不战斗者不得食！', '', 25996),
(103008, 'zhCN', '你把食物藏哪儿去了，蛆虫？！', '', 25996),
(102917, 'zhCN', '地底之王在驱使奴隶挖掘我父亲的骨头？！他不明白自己在插手什么样的力量。', '', 25996),
(103000, 'zhCN', '快，钻进桶里去！我来拖住他们，我们下游见！', '', 25996),
(102991, 'zhCN', '他们已经知道我们来了。但愿我们的盟友可以拖住他们的主力部队，让我们完成任务。', '', 25996),
(103001, 'zhCN', '不，我可不干。我自己去找路，到另一边跟你们汇合。', '', 25996),
(102979, 'zhCN', '乌拉罗格，达古尔控制不了圣锤！他只会毁灭至高岭，而不是征服它！', '', 25996),
(102983, 'zhCN', '无论如何，我都会好好享受它每一刻的。洛克莫拉，碾碎他们！', '', 25996),
(102940, 'zhCN', '纳瓦罗格？！叛徒！你想带领这些入侵者对抗我们吗？！', '', 25996),
(102918, 'zhCN', '这些卓格巴尔在使用不属于他们的能力。毫无疑问，这肯定和这些石头里残留的强大能量有关。', '', 25996),
(102899, 'zhCN', '那绝对是我所见过的最大块的水晶！', '', 25996),
(107718, 'zhCN', '', '达古尔连野生动物也不放过，被他的晶化魔法扭曲的乌龟污染了河流。快去找到这些强大怪物的源头吧。', 25996),
(102896, 'zhCN', '我父亲的古老巢穴。这些岩石依然蕴含着力量，不要惊扰它们。', '', 25996),
(102887, 'zhCN', '快，朋友。每耽搁一分钟，达古尔就会杀害我们更多的盟友。', '', 25996),
(123872, 'zhCN', '卡拉赞的能量辐射到了虚空中……就像吸引燃烧军团的明灯。', '', 25996),
(123874, 'zhCN', '还剩一场战斗。再征服一个世界，我们的伟大计划就完成了！', '', 25996),
(123873, 'zhCN', '我们的燃烧远征吞噬了无数世界。这座塔楼将成为连接它们的通道。', '', 25996),
(124955, 'zhCN', '我来打通道路。你们去对付那头恶魔，我来切断塔楼与军团世界的联系。我们最好快一点！', '', 25996),
(124951, 'zhCN', '我在这座塔的各个角落留下了许多碎片……', '', 25996),
(124954, 'zhCN', '麦迪文？', '', 25996),
(130319, 'zhCN', '', '<拼命眨眼。>', 25996),
(126693, 'zhCN', '', '对！对！对！挨揍不好受，对吧？军团的渣滓！', 25996),
(129242, 'zhCN', '你好，$n。', '', 25996),
(126925, 'zhCN', '干得好。用那些大家伙的力量对付他们自己……真是个好主意！', '', 25996),
(126924, 'zhCN', '纳迦一直是个大威胁。我们制服了他们的一个蛮兵。去以纳迦的力量对付他们自己吧！', '', 25996),
(126859, 'zhCN', '他们是自愿牺牲的，也得到了安息。你干得很好。', '', 25996),
(126858, 'zhCN', '邪能图腾的牛头人正在亵渎尸体。谁都不该遭此下场。去让他们安息吧，勇士。', '', 25996),
(126561, 'zhCN', '干得好。那些无耻的骗子干扰了重要行动。', '', 25996),
(126560, 'zhCN', '军团开启的传送门里涌出了一群恶心的小鬼。勇士，去干掉那些小恶魔！', '', 25996),
(127004, 'zhCN', '', '干得好，勇士。愿他们的灵魂安息。', 25996),
(127003, 'zhCN', '', '这些废墟里的幽灵由于某个古老的契约而受到军团的束缚。去让他们安息吧。', 25996),
(92545, 'zhCN', '', '伊利达雷是对抗燃烧军团的第一道、也是最后一道防线。', 25996),
(130135, 'zhCN', '这当然不容易，但我们设法为你的勇士们弄到了一件最高品质的装备。\n\n不幸的是，我们的补给品极其有限，所以我们在分发时一定要格外小心。', '', 25996),
(111823, 'zhCN', '你在此休息时请不要挑起是非。梦境林地是我们所有同胞的圣地。', '', 25996),
(111822, 'zhCN', '你知道“alara''shinu”吗，德鲁伊？它的意思是，发现缺憾之美。秉持这个理念生活的人，能够理解自然的变幻无常，接受生死的循环往复，并从中收获快乐。', '', 25996),
(111820, 'zhCN', '我们教派里，还有很多人记得当初莎拉达希尔在此扎根的情景。我们看着它经历诸多纪元，成长为俯视这座山谷的参天大树。我们受到它的祝福，在它的树荫庇护下生活多年。', '', 25996),
(111821, 'zhCN', '理解自然，就是理解万物的伟大循环。盛开的花朵终将零落成泥，并孕育新的种子。太阳每天落山，都是为了明日的再度升起，并带来新的生命。我们的教派就致力于理解并维护这种循环。', '', 25996),
(121509, 'zhCN', '我们等着你的命令。', '我们等着你的命令。', 25996),
(117231, 'zhCN', '森林是同呼吸共命运的整体。', '森林是同呼吸共命运的整体。', 25996);
