INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(120072, 'zhCN', '部队音效', '', 25996),
(108687, 'zhCN', '', '或许你们能赢。在拯救世界的同时，我们或许也能摆脱被迫签下的可怕协议。', 25996),
(108686, 'zhCN', '', '你们能击败军团吗？你们会失败吗？你们的时间涡流时断时续。一切都无定数！', 25996),
(108685, 'zhCN', '', '我占卜了所有的可能，但没有一个说你们能胜利。我很好奇……', 25996),
(108688, 'zhCN', '', '该死的命运！我决定赌一把，勇士们。去吧，去面对古尔丹。我会帮你们的，我们或许能拯救所有人。', 25996),
(108665, 'zhCN', '', '军团注定会获胜。所有时间流都是这么说的。如果我们反抗，他们就会强夺暗夜井，让我们彻底失去它。', 25996),
(108666, 'zhCN', '', '我不能让人民挨饿，由于无法汲取它的能量而崩溃。所以我跟他们做了笔交易。', 25996),
(108663, 'zhCN', '', '我早就预见了你们的到来。命运指引你们来到此地。为了阻止军团，你们想背水一战。', 25996),
(108668, 'zhCN', '', '你们的死将保证夜之子的未来。来吧！让我们赶紧实现你们的命运吧。', 25996),
(108667, 'zhCN', '', '最后，我看到了一个军团获胜，而我们苟且偷生的未来。我得抓紧这条命运的丝线。我要不择手段地实现它！', 25996),
(108664, 'zhCN', '', '我们面临着相似的困境。我窥探了一切可能的未来……而出路只有一条。', 25996),
(125359, 'zhCN', '这座传送门内闪烁着一座高塔的影像。', '', 25996),
(122486, 'zhCN', '闯入者！', '', 25996),
(122487, 'zhCN', '别怕，我会抽取暗夜井的力量消灭他们的！', '', 25996),
(122488, 'zhCN', '', '宫廷卫士！保护我们继续仪式！', 25996),
(118749, 'zhCN', '勇士们，你们成功了！我感觉古尔丹的仪式力量正在增长，我们得快一点了。我可以把你们传送回暗夜要塞。你们准备好了吗？', '', 25996),
(125569, 'zhCN', '艾泽拉斯的命运就在我们手中。', '', 25996),
(111412, 'zhCN', '这是我们最黑暗的时刻。', '', 25996),
(129355, 'zhCN', '看来准备得很顺利。', '', 25996),
(129353, 'zhCN', '', '但创世之柱无法带来胜利。只有兼具力量与勇气的联军才能战胜燃烧军团。等你们做好决战的准备，我的残影就会回来帮助你们。艾泽拉斯的命运就掌握就在你们手中。', 25996),
(129351, 'zhCN', '', '但还有希望。只要将创世之柱放进下方的大厅，你们就能修复我的结界，逼退入侵者。然后，你们得深入墓穴，用阿曼苏尔之眼镇压邪能风暴，并斩断军团与艾泽拉斯的联系。', 25996),
(129348, 'zhCN', '', '不会吧？守护者！', 25996),
(129350, 'zhCN', '', '我是提瑞斯法守护者，艾格文。我以这个残影作为保护，以防止我在陵墓这里设下的结界失效。没错，我感觉军团之主正在密谋着…夺取他的战利品。', 25996),
(129347, 'zhCN', '这是什么？', '', 25996),
(129349, 'zhCN', '', '多年前，圣盾保护了新生的世界之魂免遭疯狂的腐蚀。今天，它又保护了艾泽拉斯的勇士们。', 25996),
(129354, 'zhCN', '', '我们得去告诉卡德加。', 25996),
(129337, 'zhCN', '我们失败了。军团将我们逼出了墓穴。你们是保护圣盾的最后壁垒了。他们来了……', '', 25996),
(129338, 'zhCN', '让他们来吧。我和玛维来堵住阶梯。', '', 25996),
(129340, 'zhCN', '', '伊利丹，要是我的战刃刺穿你的心脏，你可别见怪。这里的恶魔太多了，我很难认出你来。', 25996),
(127936, 'zhCN', '', '你以为你逃得了吗，伊利丹？再跑的话，我就用链子把你拴起来。', 25996),
(127937, 'zhCN', '尽管装腔作势吧，玛维。但现在，请你尽力跟上我。', '', 25996),
(128820, 'zhCN', '小笨蛋也想跟军团耍花招？再想想吧！', '', 25996),
(128697, 'zhCN', '我来找你了！', '', 25996),
(127930, 'zhCN', '啊，贵客终于到了。我要拔光你脑袋上的角，背叛者！', '', 25996),
(129326, 'zhCN', '', '我们的部队正在地面与军团交战。必须在我们元气大伤前将圣盾放到塔楼里。', 25996),
(127931, 'zhCN', '', '伊利丹！见鬼，这太鲁莽了！', 25996),
(127929, 'zhCN', '你逃不出我的手心，恶魔！', '', 25996),
(122263, 'zhCN', '', '欢迎光临瘤节之枝，$n。', 25996),
(137564, 'zhCN', '各个职业大厅会将他们的所有资源与你共享，你不需要继续研究神器知识了。通过共同的努力，我们已经将所有有关神器的知识都研究完毕了。', '各个职业大厅会将他们的所有资源与你共享，你不需要继续研究神器知识了。通过共同的努力，我们已经将所有有关神器的知识都研究完毕了。', 25996),
(120267, 'zhCN', '', '树叶们担忧地窃窃私语着邪能之火和梦魇的降临，但只要你能保持坚定，那我们也不会动摇。\n\n我能帮你什么忙吗？', 25996),
(99860, 'zhCN', '我看过无数你的同胞走过人生道路，但没有一个能像你这样大踏步地前进。$B$B我期待着能向别人讲起你的故事。', '', 25996),
(117231, 'zhCN', '森林是同呼吸共命运的整体。', '森林是同呼吸共命运的整体。', 25996),
(119001, 'zhCN', '我可以帮你采购一些珍贵物品。', '我可以帮你采购一些珍贵物品。', 25996),
(121509, 'zhCN', '我们等着你的命令。', '我们等着你的命令。', 25996),
(128028, 'zhCN', '干得好。在杀了那些恶魔并夺回补给后，我们离攻占破碎海滩又近了一步。', '', 25996),
(128010, 'zhCN', '去肃清海滩上的恶魔部队，并取回那些战争物资。', '', 25996),
(128009, 'zhCN', '这片海滩上有前一次突袭中沉没的船骸，其中有对抗军团的关键物资。', '', 25996),
(130098, 'zhCN', '哈！熏死他们！我不会因此被炒掉吧？人总要保持乐观嘛。你干得还是那么好，勇士。', '', 25996),
(130097, 'zhCN', '燃烧军团想扩大地盘。如果你能扑灭这个威胁，我们会很感激的，指挥官。', '', 25996),
(115500, 'zhCN', '', '你今天让这片土地更安全了。但要小心！威胁一定会卷土重来。', 25996),
(115506, 'zhCN', '', '我们在这里发现了一个目标，小心点。', 25996),
(126693, 'zhCN', '', '对！对！对！挨揍不好受，对吧？军团的渣滓！', 25996),
(125770, 'zhCN', '我要先说明一下我们能做到什么以及做不到什么。\n\n我们可以穿过这座传送门，前往我之前所联结到的一个恶魔通道。\n\n但我们无法知道另一边有什么，因为魔法没有这个效果。\n\n所以，你先请。祝你好运，“英雄”。', '', 25996),
(126738, 'zhCN', '', '我们需要个送死的……呃……志愿者到传送门另一边去削减恶魔数量。去跟特德谈谈，他能给你点恶魔，帮你热热身。', 25996),
(129916, 'zhCN', '我其实挺喜欢鱼人的。但邪能鱼人……还是算了。', '', 25996),
(129915, 'zhCN', '邪能……鱼人？军团真是饥不择食啊。去解决它们，好吗？', '', 25996),
(130647, 'zhCN', '', '$ct，找到你喜欢的东西了吗？', 25996),
(129989, 'zhCN', '想知道打败燃烧军团的关键吗？\n\n是造房子，伙计。\n\n房子和更多的房子。哎呀！\n', '', 25996),
(130198, 'zhCN', '', '它很美，不是吗？你感觉到它的力量在你体内流淌、脉动，不断扰动我们周围的世界了吗？如果你愿意，我可以控制那股力量……', 25996),
(98620, 'zhCN', '你做好飞行的准备了吗？我们为你准备了最快的坐骑。', '', 25996),
(136300, 'zhCN', '这里充满了魔法与秘密。', '', 25996),
(137858, 'zhCN', '我曾在艾泽拉斯和德拉诺与燃烧军团战斗过，甚至见证过他们的腐蚀是怎么渗透时间流的。\n\n可在通向阿古斯的裂隙打开前，我从未想过他们会邪恶到这种程度。看看他们是怎么扭曲这个世界的……\n\n如果我们不能阻止他们，艾泽拉斯也会变成这样。', '', 25996),
(137849, 'zhCN', '邪能魔法已经渗透了整个世界。它到底还有没有未被腐化的部分，这就很难说了。', '', 25996),
(98814, 'zhCN', '查看可接的任务。', '查看可接的任务。', 25996),
(136834, 'zhCN', '', '为了强化虚空之光熔炉所做的牺牲是不会被遗忘的。', 25996),
(126362, 'zhCN', '', '让我去对付他们！', 25996),
(112042, 'zhCN', '我的警戒永不松懈。', '', 25996),
(112066, 'zhCN', '自然总是追求万物的平衡。', '', 25996),
(112010, 'zhCN', '我们要保护荒野。', '', 25996),
(112089, 'zhCN', '', '需要帮忙吗？', 25996),
(117309, 'zhCN', '我是自然之怒的化身！', '', 25996),
(119450, 'zhCN', '', '为了艾露恩！', 25996),
(119451, 'zhCN', '来吧！', '', 25996),
(112019, 'zhCN', '', '你的灵魂在熊熊燃烧。', 25996),
(120069, 'zhCN', '', '部队音效', 25996);
