INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(135556, 'zhCN', '前面潜伏着恶魔。以艾泽拉斯之名打倒它！', '', 26365),
(136787, 'zhCN', '', '局势很严峻。$b$b从这里直接发动攻击的代价太高了。而且，城墙上那些火炮非常强大，足以击落维迪卡尔。$b$b我建议利用这座要塞来发动游击战。刺杀他们的重要领袖，在他们的部队中间制造破坏。$b$b我知道这不是你和图拉扬想要听到的，但耐心的猎人必须懂得见机行事。', 26365),
(136783, 'zhCN', '', '欢迎来到阿古斯的心脏。', 26365),
(136781, 'zhCN', '', '这里有许多猎物。', 26365),
(135785, 'zhCN', '这样就行了！我这就把坐标加入道标矩阵。', '', 26365),
(135239, 'zhCN', '我们终于扭转了局面。让军团在我们的攻势下瑟瑟发抖吧！', '', 26365),
(135238, 'zhCN', '千里之堤，溃于蚁穴……', '', 26365),
(137473, 'zhCN', '没有足够的能量，我只能做一些……临时发挥了。我这大技师的名字可不是白叫的！让矩阵核心过载，然后重构……$b$b这不重要。最重要的是火炮还可以用。我们会打碎安托鲁斯的大门，击败军团的。$b$b你准备好了吗，$n？', '', 26365),
(135235, 'zhCN', '军团的心脏……我很快就会把它挖出来。', '', 26365),
(135232, 'zhCN', '军团入侵了你们的世界。现在，反击的时刻到了。', '', 26365),
(135767, 'zhCN', '虽然形体破碎了，但是，她的圣光还未离去。', '', 26365),
(135775, 'zhCN', '不能让她的圣光堕入黑暗。我们得尽量把她的遗体送进熔炉里去。', '', 26365),
(137939, 'zhCN', '', '刚才出什么事了？他……杀了她？', 26365),
(137940, 'zhCN', '我简直不敢相信我的眼睛……', '', 26365),
(135766, 'zhCN', '你说的对，先知。我们还有任务。', '', 26365),
(135761, 'zhCN', '我们还有任务，我们还有世界要去拯救。我们必须前行！', '', 26365),
(135760, 'zhCN', '圣光不会随着始祖纳鲁死去。它闪耀在我们心中！', '', 26365),
(137941, 'zhCN', '', '这真是……出人意料。没想到他的力量居然能摧毁她……', 26365),
(137944, 'zhCN', '<图拉扬狠狠地瞪着伊利丹。>', '', 26365),
(132874, 'zhCN', '有了圣光之心，我们就能让泽拉恢复鼎盛状态了。$b$b这件事越快越好，这样我们的力量就能早日超过燃烧军团。', '', 26365),
(132388, 'zhCN', '把道标放到我的位置。我们要回家了。', '', 26365),
(132389, 'zhCN', '我们的部队会夺回泽尼达尔。虽然我们找回了最珍贵的东西，但也不能让这艘船落入军团之手。', '', 26365),
(136701, 'zhCN', '任务完成了。回维迪卡尔吧，我们还有很多事要做。', '', 26365),
(132697, 'zhCN', '泽拉看到你冒险来救她，一定会重新信任你的——我敢肯定！', '', 26365),
(132633, 'zhCN', '', '然后呢？我曾因为违背她的意愿研究虚空而被关进牢笼。她会再把我关起来吗？甚至更糟？', 26365),
(132695, 'zhCN', '我感觉泽拉在中央大厅里。感谢圣光！我们没来迟。', '', 26365),
(135688, 'zhCN', '成了！最后激活一次控制台就能完成传送了。剩下的交给我！', '', 26365),
(134677, 'zhCN', '我查到你的坐标了，勇士。校准传送矩阵需要一点时间。在那之前，别让它落入军团之手！', '', 26365),
(132438, 'zhCN', '他们又包围了我们。可怜的蠢货。', '', 26365),
(132436, 'zhCN', '只要那些邪恶的恶魔还能喘气，就没有和平可言。$b$b只要那些无信的叛徒还活着，就不会有宽恕存在。', '', 26365),
(132437, 'zhCN', '每当我的猎物跑掉时，我也有这种感觉。不过它们通常只是垂死挣扎而已。', '', 26365),
(132443, 'zhCN', '圣光指引着我的箭。圣光的炽热足以令黑暗无所遁形。', '', 26365),
(132421, 'zhCN', '', '在见识过诱惑了我们兄弟的力量后，我们中只剩下少数人还掌握着真正的魔法。我会利用一切能够获得的优势。', 26365),
(132423, 'zhCN', '', '那边的波莫斯对于这个时代有一句名言：“给我一百个光铸者。否则，就给我一千常规部队。”', 26365),
(132422, 'zhCN', '', '我们依靠剑与魔法抗衡敌人。我们不能过于依赖其中一种。', 26365),
(132425, 'zhCN', '', '我们的人民曾经很贪婪，非常渴望力量。而我信仰圣光，而且我拥有足够的力量让他们明白自己的道路是错误的。', 26365),
(132175, 'zhCN', '', '只要军团还在，我就不能死，大主教。跟我来。我们设置了一个结界。', 26365),
(132176, 'zhCN', '你哪儿都别想去，“勇士”。主人已经决定了你的下场！', '', 26365),
(132162, 'zhCN', '法瑞娅！你还活着！', '', 26365),
(132172, 'zhCN', '', '放弃吧，怪物！今日圣光必胜！', 26365),
(131495, 'zhCN', '我们得团结起来，才能打败军团，酋长。现在，我们的朋友正身处险境。', '', 26365),
(132159, 'zhCN', '我到前面去开路。通向泽尼达尔的桥上似乎没有多少守卫。愿圣光保佑我们。', '', 26365),
(131501, 'zhCN', '我们一直在默默蛰伏，不断适应。在我们有需要时，这片土地仍然会回应我们的祈求。', '', 26365),
(131499, 'zhCN', '一条隧道！难怪你们能生存下来……', '', 26365),
(133825, 'zhCN', '我的母亲肯定会问起希尔瓦娜斯的事。你打算怎么跟她说？', '', 26365),
(131422, 'zhCN', '我……明白。愿圣光保佑你们。', '', 26365),
(131421, 'zhCN', '孩子，我们还有很多话要说，但泽尼达尔上的盟友正危在旦夕。', '', 26365),
(132632, 'zhCN', '', '你得去帮他们，图拉扬。我到敌后去引开恶魔。我们很快就回来，阿拉托尔。', 26365),
(131423, 'zhCN', '', '小心点。我们去找其他人，为之后的战斗做准备。', 26365),
(133826, 'zhCN', '', '这是躲不开的话题，可一想到它，我就惶恐。我该怎么向奥蕾莉亚解释我们的姐妹成了一个怪物？真相会让她心碎的。', 26365),
(133761, 'zhCN', '', '耐心点，阿拉托尔。这需要时间，但你们会团聚的。相信我。', 26365),
(131488, 'zhCN', '他们的离开让我心痛，温蕾萨阿姨。我等待多年，不能在这个时候失去他们。', '', 26365),
(136392, 'zhCN', '对，呃，你可难住我了。不过这样还是很舒服的。$B$B如果你不介意的话，我还有很多东西要读。', '', 26365),
(136304, 'zhCN', '脚下冰冷的地面能帮助我保持专注。', '', 26365),
(136245, 'zhCN', '那个？那只是我召唤出来的传送门，能够让你安全地往返达拉然。$B$B天知道我们什么时候会需要援军！', '', 26365),
(136390, 'zhCN', '', '我很好，$n。谢谢你的关心。$B$B在你走上战场前，我想告诉你，如果你需要找个地方休息，尽管来找我帮忙。$B$B保重。', 26365),
(137467, 'zhCN', '多年以前，我的人民所居住的世界被虚空吞噬，在狂风中粉碎。我能感觉到一些老朋友的面孔就快在阿古斯上出现了。$b$b当然，我不是说他们的脸。', '', 26365),
(136244, 'zhCN', '', '我身后的是虚空之光熔炉。它是这个世界的古老遗物，曾为这个世界提供了大量魔力。$B$B虽然它的潜力被许多人视为一种恩赐，但强化它却需要巨大的牺牲……以及光与影的微妙平衡。', 26365),
(131889, 'zhCN', '可是……你们为何不联系我们？联系同胞？联系我？', '', 26365),
(134221, 'zhCN', '', '一千年来，我每天都在想应该跟他说什么。可现在……我却什么都说不出来。', 26365),
(134222, 'zhCN', '我知道，亲爱的。我也是这样。但只要见到他，我们自然就会知道该说什么了。', '', 26365),
(130662, 'zhCN', '', '阿拉托尔！他也来了！', 26365),
(130661, 'zhCN', '艾泽拉斯的飞船抵达时，我感觉到了他的存在。虽然分开了那么久，但是我不会搞错的。', '', 26365),
(130029, 'zhCN', '', '你没事吧，图拉扬？', 26365),
(131669, 'zhCN', '', '它变虚弱了。全力攻击！', 26365),
(130217, 'zhCN', '集中攻击。我来吸引他的注意。', '', 26365),
(130001, 'zhCN', '奥蕾莉亚！你总是那么准时。', '', 26365),
(130027, 'zhCN', '', '我都习惯从各种恶魔的手里把你救出来了。', 26365),
(130116, 'zhCN', '烈焰的仆从们！享用他们的血肉吧！', '', 26365),
(129997, 'zhCN', '你不是我们的对手！', '', 26365),
(129994, 'zhCN', '别放松！我们今天就了结这头野兽！', '', 26365),
(129992, 'zhCN', '我跟你们战斗了一千年，恶魔。就算你大言不惭，也难逃一死！', '', 26365),
(129991, 'zhCN', '肌肉无力，骨骼脆弱的废物！', '', 26365),
(129990, 'zhCN', '你们就这点本事吗？真可悲……', '', 26365),
(130014, 'zhCN', '今天就是你的死期，恶魔！', '', 26365),
(129984, 'zhCN', '你们的灵魂都将燃烧！', '', 26365),
(129987, 'zhCN', '我们不能在这里滞留太久。', '', 26365),
(130483, 'zhCN', '傲慢的凡人！来吧……受死吧！', '', 26365),
(129853, 'zhCN', '图拉扬！很高兴看到你再次挫败了军团暗杀你的阴谋。', '', 26365),
(129855, 'zhCN', '我上一次见到她还是在泽尼达尔。她还在……接受监控。', '', 26365),
(129854, 'zhCN', '奥蕾莉亚呢？她还……', '', 26365),
(129857, 'zhCN', '但我们和盟友间还隔着数以千计的恶魔。我们得削减军团的兵力，才有希望活着抵达泽尼达尔。', '', 26365),
(129858, 'zhCN', '那我们就行动吧，大主教。', '', 26365),
(129856, 'zhCN', '老朋友，奥蕾莉亚肯定就在某个地方。其他幸存者也一样。', '', 26365),
(132371, 'zhCN', '其他人就没这么幸运了。他们还在坠毁现场坚守，为了活命而战斗。', '', 26365),
(132370, 'zhCN', '泽尼达尔坠毁时，哈顿的人救了我。', '', 26365),
(129666, 'zhCN', '这座小屋……很眼熟。', '', 26365),
(129664, 'zhCN', '跟紧我。有人在监视我们。', '', 26365),
(129578, 'zhCN', '小心，勇士。军团很擅长欺骗。', '', 26365),
(129579, 'zhCN', '我们可能踏入了陷阱。', '', 26365),
(132386, 'zhCN', '那些发现真相的人试图逃跑，结果却被困在这梦魇中。我都不知道他们是怎么活下来的。', '', 26365),
(135973, 'zhCN', '这边！他们肯定就在附近。', '', 26365),
(132382, 'zhCN', '你高估了他们的忠诚。如果有必要，那你就冒次险吧。我来整顿部队，迎接反击。', '', 26365),
(132383, 'zhCN', '好吧。走吧，$p。我感觉他们就在不远处。', '', 26365),
(132385, 'zhCN', '许多人被萨格拉斯许诺的力量和荣耀所蒙骗，自愿加入了军团。', '', 26365),
(132380, 'zhCN', '他们可以成为宝贵的盟友。', '', 26365),
(132384, 'zhCN', '阿克蒙德和基尔加丹与黑暗泰坦达成交易时，我的人民没能全部逃离。', '', 26365),
(129121, 'zhCN', '我们再也扛不住那些怪物的下一轮攻击了。得支援我们的部队。', '', 26365),
(135960, 'zhCN', '那些克罗库奴隶都逃到山下某个地方去了。他们本来不可能跑那么远的。', '', 26365),
(132283, 'zhCN', '很快，军团就会蜂拥而至。', '', 26365),
(132704, 'zhCN', '小心！', '', 26365),
(132284, 'zhCN', '我们会坚守阵地的。但愿圣光军团还有幸存者。', '', 26365),
(132705, 'zhCN', '无论那是什么，我相信萨格拉斯都不会只有这一个。', '', 26365),
(132303, 'zhCN', '抓紧，$p。这个巨人动了！', '', 26365),
(132301, 'zhCN', '尝尝积累了一万年的仇恨吧！', '', 26365),
(132298, 'zhCN', '吸引它的注意，伊利丹。我们不能功亏一篑！', '', 26365),
(132299, 'zhCN', '我还能做得更好。', '', 26365),
(132286, 'zhCN', '圣光在上……', '', 26365),
(135926, 'zhCN', '我们得联手。为了艾泽拉斯！', '', 26365),
(135911, 'zhCN', '这不仅仅是攻城武器……', '', 26365),
(135905, 'zhCN', '让我们抓紧机会摧毁这台邪恶的机器吧。', '', 26365),
(135910, 'zhCN', '有问题。', '', 26365),
(135909, 'zhCN', '他们的武器附近没有守卫吗？', '', 26365),
(132748, 'zhCN', '接受圣光的净化吧！', '', 26365),
(132749, 'zhCN', '见证维迪卡尔的力量吧！', '', 26365),
(132747, 'zhCN', '道路肃清了。开火！', '', 26365),
(135881, 'zhCN', '救命……帮帮……我……', '', 26365),
(135880, 'zhCN', '那些恶魔体内充斥着邪能。军团在远征期间，也没在这里浪费时间啊。', '', 26365),
(135878, 'zhCN', '他们中有些是最强大的艾瑞达战士。千万不要低估了他们。', '', 26365),
(129117, 'zhCN', '我们守不了这条阵线多久了。', '', 26365),
(135877, 'zhCN', '在屏障消失前，维迪卡尔无法轰炸那些灭绝者。快！', '', 26365),
(132469, 'zhCN', '守住阵地！我们要用生命保护维迪卡尔！', '', 26365),
(136143, 'zhCN', '不，圣光在上，不要让我们的努力白费！', '', 26365),
(136139, 'zhCN', '', '我们的故乡……它怎么了？', 26365),
(132419, 'zhCN', '正在激活传送阵列。', '', 26365),
(135876, 'zhCN', '$p，你准备好之后，我们就一起下去。', '', 26365),
(135873, 'zhCN', '罗姆尔，派我们的先锋去开辟一条战线吧。', '', 26365),
(132420, 'zhCN', '做好准备，$p。', '', 26365),
(132413, 'zhCN', '下次轰炸恐怕会有重大伤亡——我们得先出击。', '', 26365),
(135872, 'zhCN', '道标已放出，先知！', '', 26365),
(135871, 'zhCN', '我们得趁那件武器还在充能时摧毁它！罗姆尔，放个道标下去！', '', 26365),
(135870, 'zhCN', '你准备好离开了就告诉我一声。', '', 26365),
(130103, 'zhCN', '我的人民一直在等着这一天的到来。', '', 26365),
(135862, 'zhCN', '圣光会带我们找到他们。罗姆尔，开始最后的准备，别忘了带上部队，是时候了。', '', 26365),
(135863, 'zhCN', '遵命，先知。我这就去。', '', 26365),
(135845, 'zhCN', '圣光军团的兄弟们正等着我们。我们必须团结才能取得胜利。', '', 26365),
(135840, 'zhCN', '通道打开了。同胞们，盟友们，勇士们，维迪卡尔在等着我们！', '', 26365),
(135839, 'zhCN', '我们用所有的时间和资源造了一艘新船。它将是我们重获新生的依仗。', '', 26365),
(135838, 'zhCN', '埃索达一直没有从多年前的那次坠毁事故中恢复过来。', '', 26365),
(135835, 'zhCN', '助我一臂之力，$p。我们一起打开通道。', '', 26365),
(131073, 'zhCN', '没错，伊利丹。这可能是我们占得先机的最佳机会。', '', 26365),
(131059, 'zhCN', '军团已经孤注一掷了。我们应该趁他们还在关注艾泽拉斯时果断出击。', '', 26365),
(131058, 'zhCN', '来吧，勇士。让圣光指引你！', '', 26365),
(135823, 'zhCN', '德莱尼们……艾泽拉斯的勇士们……反攻阿古斯的时机到了。', '', 26365),
(135824, 'zhCN', '我们要拯救我们的世界，并且……彻底阻止燃烧军团。', '', 26365),
(131084, 'zhCN', '先知维伦就在前面，勇士。', '先知维伦就在前面，勇士。', 26365),
(135807, 'zhCN', '阿古斯是我们世代的家园。不能让军团继续占有它。', '', 26365),
(135808, 'zhCN', '然后我的父母就能回家了。', '', 26365),
(135805, 'zhCN', '来吧，我带你去圣光穹顶。其他人正在等着我们呢。', '', 26365),
(135806, 'zhCN', '', '我从未见过德莱尼集结起这样的大军。', 26365),
(131051, 'zhCN', '', '看来他们还是出现了。', 26365),
(130807, 'zhCN', '', '我不在乎阿古斯有多么恐怖。只要有机会在那里找到奥蕾莉亚，我就跟你们去。', 26365),
(130856, 'zhCN', '我们必须找到圣光军团。在他们的协助下，军团必将覆灭。我们必须找到……', '', 26365),
(132489, 'zhCN', '你的父母还活着，阿拉托尔，我肯定。但现在，专心迎接之后的战斗吧。', '', 26365),
(130826, 'zhCN', '', '现在就看部落会不会响应维伦的号召了。那些野蛮人缺乏荣誉感，但是很擅长战斗。', 26365);
