INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES
(46786, 0, 0, 0, 0, 0, 0, 0, 0, '我们的力量在增长。', 25996), -- -Unknown-
(46044, 1, 66, 0, 0, 0, 500, 0, 0, '你瞧，$ct，别误会了。我爱艾维娜。她是我的半神！但是她有点太被动了，让作为追随者的我提不起劲儿来。$b$b而你呢？你做的才是实事！我想要战斗，找恶魔的麻烦！$b$b怎么样？你愿意带上我吗？', 25996), -- -Unknown-
(45425, 1, 2, 0, 0, 0, 3, 0, 0, '过去的几个月里，我们已经请你帮了很多忙，$n。而你的表现不仅没有让我们失望，还让我们喜出望外。你消灭了卡提萨克斯，阻止了对半神的攻击。$b$b你是当之无愧的梦境林地大德鲁伊。我很荣幸能与你共事，希望我们能联手为这个世界做出更多贡献。', 25996), -- -Unknown-
(46677, 1, 0, 0, 0, 0, 0, 0, 0, '复仇的感觉真棒。$b$b但是阻止了对半神的攻击感觉更棒。', 25996), -- -Unknown-
(46676, 1, 0, 0, 0, 0, 0, 0, 0, '知道我们成功救助其他的一些德鲁伊后，我感觉好多了。 我们已经尽力了。', 25996), -- -Unknown-
(46675, 1, 0, 0, 0, 0, 0, 0, 0, '我来看看……我想我搞清楚了！', 25996), -- -Unknown-
(46674, 0, 0, 0, 0, 0, 0, 0, 0, '看他扭动的样子。他这么容易就屈服了！', 25996), -- -Unknown-
(47030, 0, 0, 0, 0, 0, 0, 0, 0, '谢谢你前来。我有东西想给你看。', 25996), -- -Unknown-
(47035, 0, 0, 0, 0, 0, 0, 0, 0, '谢谢你前来。我有东西想给你看。', 25996), -- -Unknown-
(47034, 0, 0, 0, 0, 0, 0, 0, 0, '谢谢你前来。我有东西想给你看。', 25996), -- -Unknown-
(45426, 1, 0, 0, 0, 0, 0, 0, 0, '关于艾维娜圣殿遇袭的事件，你有什么消息吗？', 25996), -- -Unknown-
(46924, 1, 0, 0, 0, 0, 0, 0, 0, '我们必须警告梦境林地，要把这里发生的事告诉他们。', 25996), -- -Unknown-
(45498, 1, 0, 0, 0, 0, 0, 0, 0, '我们成功了！我们救出了戈德林！', 25996), -- -Unknown-
(45528, 1, 0, 0, 0, 0, 0, 0, 0, '这下恶魔就没法呼叫援军了。真棒！', 25996), -- -Unknown-
(44921, 1, 0, 0, 0, 0, 0, 0, 0, '如果戈德林在梦境中，那他就无力自保！我们得去帮他！', 25996), -- -Unknown-
(44888, 1, 0, 0, 0, 0, 0, 0, 0, '大自然急需帮助，凡人。你必须拨乱反正。', 25996), -- -Unknown-
(44877, 4, 0, 0, 0, 0, 0, 0, 0, '哈！接招吧，恶魔渣滓！', 25996), -- -Unknown-
(45532, 6, 0, 0, 0, 0, 0, 0, 0, '她说了声东击西？！听起来不妙啊……', 25996), -- -Unknown-
(44869, 1, 0, 0, 0, 0, 0, 0, 0, '我费尽全力才忍住了不等援军就冲入战场的冲动。我们上吧！', 25996), -- -Unknown-
(47007, 0, 0, 0, 0, 0, 0, 0, 0, '干得好，我现在可以赐福于你，只要法师塔处于激活状态，你就可以获得增益效果。', 25996), -- -Unknown-
(46772, 0, 0, 0, 0, 0, 0, 0, 0, '我们付出的卓绝努力换来了赐福，在接下来的日子里，我们大家都能从中收益。', 25996), -- -Unknown-
(48272, 1, 0, 0, 0, 0, 0, 0, 0, '万神殿已经死去数千年……我恐怕这个计划已经筹备很久很久了。', 25996), -- -Unknown-
(48273, 1, 0, 0, 0, 0, 0, 0, 0, '感谢你的到来，$G小伙子:丫头;。有些东西必须让你看下。', 25996), -- -Unknown-
(47654, 2, 1, 0, 0, 0, 0, 0, 0, '完成了。我为鲁拉的逝去而哀伤，但你至少让她获得了安宁。$b$b有了执政团之冠，维迪卡尔就能够承受住燃烧军团的凶狠攻击了。安托鲁斯必将陨落！', 25996), -- -Unknown-
(49224, 1, 0, 0, 0, 0, 0, 0, 0, '你的武器变得越强，我们就能够给它灌注更多的力量。不过你的武器新获得的力量目前应该够用了。', 25996), -- -Unknown-
(48560, 1, 1, 0, 0, 0, 0, 0, 0, '啊，没错。正如先知所说，平衡才是关键。', 25996), -- -Unknown-
(47220, 1, 0, 0, 0, 0, 0, 0, 0, '你带来了什么，$n？$b$b<维伦垂眼凝视这个冰冷致密的物体。>$b$b有意思……', 25996), -- -Unknown-
(47219, 1, 0, 0, 0, 0, 0, 0, 0, '<奥蕾莉亚把心脏握在手中。>$b$b这就是我接下来需要做的事情，$n。谢谢你陪我来到这里。', 25996), -- -Unknown-
(47218, 1, 0, 0, 0, 0, 0, 0, 0, '很高兴我们拥有共同的目标。也许你的种族会有点作用。', 25996), -- -Unknown-
(47217, 1, 0, 0, 0, 0, 0, 0, 0, '我们赢得了时间，但也仅此而已。', 25996), -- -Unknown-
(47203, 0, 0, 0, 0, 0, 0, 0, 0, '<奥蕾莉亚看起来沉默了片刻。>', 25996), -- -Unknown-
(47184, 1, 0, 0, 0, 0, 0, 0, 0, '我说过，虚空的影响一般都很狂暴。我们必须继续前进。', 25996), -- -Unknown-
(47183, 1, 0, 0, 0, 0, 0, 0, 0, '我本应该想到的。', 25996), -- -Unknown-
(47100, 25, 0, 0, 0, 0, 0, 0, 0, '<这名破碎者怀疑地望向他的周围，避免与你视线接触。>', 25996), -- -Unknown-
(47180, 1, 0, 0, 0, 0, 0, 0, 0, '我可以感觉到虚空的力量在消退。或许我们现在可以弄清发生了什么。', 25996), -- -Unknown-
(40761, 1, 0, 0, 0, 0, 0, 0, 0, '这里感觉有些不对劲。', 25996), -- -Unknown-
(47101, 1, 0, 0, 0, 0, 0, 0, 0, '这是……圣光布道吗？有意思。', 25996), -- -Unknown-
(47238, 1, 0, 0, 0, 0, 0, 0, 0, '这真是个重磅消息。从这里开始，还是听从奥蕾莉亚的指引比较明智。', 25996), -- -Unknown-
(47416, 0, 0, 0, 0, 0, 0, 0, 0, '我很担心鲁拉，但这个真的是……无法想象。$b$b她承受了怎样的痛苦啊……', 25996), -- -Unknown-
(47856, 0, 0, 0, 0, 0, 0, 0, 0, '完成了。在众多错误之中，至少这一个错误终于修正了。', 25996), -- -Unknown-
(47855, 0, 0, 0, 0, 0, 0, 0, 0, '<先知维伦严肃地看着你。>$b$b感谢你。', 25996), -- -Unknown-
(47853, 0, 0, 0, 0, 0, 0, 0, 0, '<维伦看着面前破碎的雕像，沉默不语。>', 25996), -- -Unknown-
(48345, 0, 0, 0, 0, 0, 0, 0, 0, '<维伦似乎没有注意到你的存在。>', 25996), -- -Unknown-
(47854, 1, 0, 0, 0, 0, 0, 0, 0, '如果我对燃烧军团还算有所了解，那么我可以说，除了反击，我们别无选择。$b$b很高兴你能站在我这边。', 25996), -- -Unknown-
(47995, 396, 273, 0, 0, 0, 0, 0, 0, '法瑞娅的部队是圣光军团的尖兵队伍。有他们的帮助，我们的任务成功率将大大提升。', 25996), -- -Unknown-
(47691, 1, 0, 0, 0, 0, 0, 0, 0, '你在我沉思的时候来找我。$b$b<维伦表情疲惫地盯着你。>$b$b没时间多愁善感了。我们必须继续前进。', 25996), -- -Unknown-
(48344, 1, 0, 0, 0, 0, 0, 0, 0, '绝不容失误，$n。', 25996), -- -Unknown-
(48461, 1, 0, 0, 0, 0, 0, 0, 0, '有那么多军团世界等待我们前去占领……', 25996), -- -Unknown-
(48107, 1, 0, 0, 0, 0, 0, 0, 0, '我们还差一点就能重现执政团之冠往日的辉煌了。', 25996), -- -Unknown-
(48027, 1, 0, 0, 0, 0, 0, 0, 0, '看来你想采集星辰之耀。没错，采集这种草药的确要讲方法。$b$b这种花朵蕴含着某种力量，让它可以保护自己免受摧毁。如果它真的感觉到了威胁，就会自我毁灭。$b$b你不能抱着伤害它的心去采集，而是要想着保护。讽刺的是，我们用它制作物品的时候，还是必须将它摧毁。$b$b但为了卸除它的防备，你必须学会这种伪装。', 25996), -- -Unknown-
(47690, 273, 6, 0, 0, 0, 0, 0, 0, '我从未感受过这种力量……$b$b<伊米拉警惕地摆弄着碎片。>$b$b我们应该尽快把这东西带给维伦。', 25996), -- -Unknown-
(47685, 0, 0, 0, 0, 0, 0, 0, 0, '哦。你成功了。$b$b真是出乎意料。', 25996), -- -Unknown-
(47687, 0, 0, 0, 0, 0, 0, 0, 0, '我得承认，你做得不错。', 25996), -- -Unknown-
(47689, 0, 0, 0, 0, 0, 0, 0, 0, '如果你能坚持那么久的话，也许有资格知道学宫的秘密。', 25996), -- -Unknown-
(47883, 0, 0, 0, 0, 0, 0, 0, 0, '做好准备，陌生人。', 25996), -- -Unknown-
(47688, 274, 0, 0, 0, 0, 0, 0, 0, '我没想到最终的产品会如此的……粘稠。$b$b<伊米拉的双手在长袍上擦了擦。>$b$b会有作用的。', 25996), -- -Unknown-
(47882, 273, 0, 0, 0, 0, 0, 0, 0, '是的，非常好。导体又恢复供能了。$b$b然而这个构造体比我想象的要更难唤醒。', 25996), -- -Unknown-
(47686, 1, 0, 0, 0, 0, 0, 0, 0, '这地方有自己的秘密。好在我知道足够多的秘密，可以开始了。', 25996), -- -Unknown-
(46941, 1, 0, 0, 0, 0, 0, 0, 0, '前方的道路已经敞开，我们还有圣光军团的协助。', 25996), -- -Unknown-
(47066, 0, 0, 0, 0, 0, 0, 0, 0, '<维伦盯着你，神情困扰。>', 25996), -- -Unknown-
(46818, 1, 0, 0, 0, 0, 0, 0, 0, '形势已经变了。我们必须向前推进。', 25996), -- -Unknown-
(46834, 1, 0, 0, 0, 0, 0, 0, 0, '我无法改变过去发生在这里的悲剧，但这多少能让我得到一些安慰。$b$b谢谢你，$n。', 25996), -- -Unknown-
(46815, 6, 0, 0, 0, 0, 0, 0, 0, '战斗似乎永无终结。他们像这样受苦有多久了？', 25996), -- -Unknown-
(48081, 0, 0, 0, 0, 0, 0, 0, 0, '<维伦的视线被他眼前的废墟所吸引。>$b$b我回家了。', 25996), -- -Unknown-
(47994, 0, 0, 0, 0, 0, 0, 0, 0, '既然机甲已经接受过彻底的测试，我会让技师们再次启动它，以便在战斗中协助你。如果你愿意，有空的时候可以在矩阵核心中进行选择。', 25996), -- -Unknown-
(47993, 1, 0, 0, 0, 0, 0, 0, 0, '圣光在上……', 25996), -- -Unknown-
(47992, 0, 0, 0, 0, 0, 0, 0, 0, '漂亮的一仗！接下来就是指挥官了。', 25996), -- -Unknown-
(47990, 71, 0, 0, 0, 0, 0, 0, 0, '没错！就是这个！', 25996), -- -Unknown-
(47989, 2, 0, 0, 0, 0, 0, 0, 0, '厉害。没有破坏魔强化他们的力量，我们或许会有胜算。', 25996), -- -Unknown-
(47991, 396, 0, 0, 0, 0, 0, 0, 0, '单靠一次行动无法赢得战争，但没有了那些尖塔的保护，要塞的其他地方很快就将落入我们手中。', 25996), -- -Unknown-
(47987, 0, 0, 0, 0, 0, 0, 0, 0, '谢谢你，$n。你的努力遏制了军团的力量，让死者得到应有的安息。', 25996), -- -Unknown-
(47986, 113, 0, 0, 0, 0, 0, 0, 0, '太棒了！谢谢你，$n。', 25996), -- -Unknown-
(47988, 396, 0, 0, 0, 0, 0, 0, 0, '他们的部队已经陷入混乱，我们继续前进吧。', 25996), -- -Unknown-
(47892, 1, 0, 0, 0, 0, 0, 0, 0, '如果不快点行动，这里的恶魔很快就会淹没我们。', 25996), -- -Unknown-
(47891, 6, 0, 0, 0, 0, 0, 0, 0, '麦格尼给你看了什么？', 25996), -- -Unknown-
(47890, 661, 661, 0, 0, 0, 0, 0, 0, '我不知道萨格拉斯是如何复活了阿格拉玛，但相信我，这是个糟糕的消息。', 25996), -- -Unknown-
(47889, 1, 0, 0, 0, 0, 0, 0, 0, '谢谢你愿意和我一起，$G小伙子:丫头;。你得来看看这个。', 25996), -- -Unknown-
(48929, 396, 0, 0, 0, 0, 0, 0, 0, '<图拉扬严肃地倾听着。>', 25996), -- -Unknown-
(48202, 396, 0, 0, 0, 0, 0, 0, 0, '恶魔众多。要干的活儿可不少。', 25996), -- -Unknown-
(48200, 1, 0, 0, 0, 0, 0, 0, 0, '很好。这样我们就能快速来回增援两个营地了。', 25996), -- -Unknown-
(49014, 1, 0, 0, 0, 0, 0, 0, 0, '经过这么多年的奋斗，这么多生命的牺牲……胜利终于近在咫尺。', 25996), -- -Unknown-
(48199, 1, 0, 0, 0, 0, 0, 0, 0, '我没想到会出现这样的毁灭…这样的恐怖。', 25996), -- -Unknown-
(47287, 113, 0, 0, 0, 0, 0, 0, 0, '当你使用矩阵上行链路时，圣光裁决者将会按照你的命令开火。$b$b也许旅程之中你会发现更多的东西可以连接到核心中。我已经把足够的能量引导到了核心的那个部分，以便处理你能连接的所有东西。$b$b祝你一切顺利，我的朋友。', 25996), -- -Unknown-
(48559, 1, 0, 0, 0, 0, 0, 0, 0, '虽然这只是第一步，但至关重要，$n。$b$b如果我们为虚空之光熔炉带来平衡，就一定能把你的武器变得更强大。', 25996), -- -Unknown-
(49143, 1, 0, 0, 0, 0, 0, 0, 0, '啊，很好，我从这些碎片中感觉到了圣光的力量。别灰心，$n！', 25996), -- -Unknown-
(47743, 1, 0, 0, 0, 0, 0, 0, 0, '我没想到泽拉会这样做，也没想到伊利丹会有这样的反应。$b$b我们一定不能让这些事件击溃我们盟友的士气。', 25996), -- -Unknown-
(47653, 1, 0, 0, 0, 0, 0, 0, 0, '圣光军团还在。圣光之母的预言将会实现。$b$b一切都是注定的。', 25996), -- -Unknown-
(47652, 5, 0, 0, 0, 0, 0, 0, 0, '赞美圣光！今天是黑暗的一天，许多人牺牲了生命。$b$b但圣光军团仍然还在。圣光之母的预言将会实现。$b$b一切都是注定的。', 25996), -- -Unknown-
(47754, 1, 0, 0, 0, 0, 0, 0, 0, '干得漂亮。道路已经清理好了。', 25996), -- -Unknown-
(47526, 396, 0, 0, 0, 0, 0, 0, 0, '<面板闪烁着柔和的金色光芒。根据罗姆尔的说法，激活它之后应该可以把火炮传送到维迪卡尔上。>', 25996), -- -Unknown-
(47771, 396, 0, 0, 0, 0, 0, 0, 0, '这里的压力太大了，而且离我们太近……', 25996), -- -Unknown-
(47508, 396, 0, 0, 0, 0, 0, 0, 0, '我们虽然被困在地上，但他们也别想控制天空！', 25996), -- -Unknown-
(47541, 396, 0, 0, 0, 0, 0, 0, 0, '你战斗很英勇。多亏你的努力，圣光更加明亮了。', 25996), -- -Unknown-
(40238, 1, 0, 0, 0, 0, 0, 0, 0, '干得好。他们还会继续修筑传送门，但我们可以利用目前的优势。', 25996), -- -Unknown-
(46213, 396, 0, 0, 0, 0, 0, 0, 0, '你带来了水晶？赞美圣光，我们需要的就是这个！', 25996), -- -Unknown-
(47431, 1, 0, 0, 0, 0, 0, 0, 0, '我们获得了落脚点。很好。$b$b是时候和他们开战了！', 25996), -- -Unknown-
(48013, 1, 0, 0, 0, 0, 0, 0, 0, '你真是一名值得尊敬的盟友，$n。多谢你的帮助。$b$b按照约定，这是药水的配方。', 25996), -- -Unknown-
(48500, 0, 0, 0, 0, 0, 0, 0, 0, '准备好继续我们的旅程了吗？', 25996), -- -Unknown-
(46843, 0, 0, 0, 0, 0, 0, 0, 0, '我们的离开伤害了阿拉托尔，我们深知这点。但只有得知他是安全的，我们才能安心地对抗军团。$b$b现在我们团聚在一起，我决不会让任何事情将我们分开了。', 25996), -- -Unknown-
(46842, 1, 1, 0, 0, 0, 0, 0, 0, '今天的胜利是迈向最终凯旋的第一步，$ct。', 25996), -- -Unknown-
(46841, 0, 0, 0, 0, 0, 0, 0, 0, '现在他们的领主已经灰飞烟灭，我们可以直击他们的力量中心。', 25996), -- -Unknown-
(46840, 0, 0, 0, 0, 0, 0, 0, 0, '哈顿的人民会非常感激你为他们所做的一切。', 25996), -- -Unknown-
(46839, 0, 0, 0, 0, 0, 0, 0, 0, '好戏开场了。', 25996), -- -Unknown-
(46816, 1, 0, 0, 0, 0, 0, 0, 0, '洛萨克森是一位高贵的战士，也是我们的老朋友了。有他与我们并肩作战实属幸运。', 25996), -- -Unknown-
(46732, 1, 0, 0, 0, 0, 0, 0, 0, '图拉扬还活着！看来我们或许还有希望。', 25996), -- -Unknown-
(47641, 1, 0, 0, 0, 0, 0, 0, 0, '肯定就是这里了。', 25996), -- -Unknown-
(47627, 396, 0, 0, 0, 0, 0, 0, 0, '看来我们低估了燃烧军团的狡诈。', 25996), -- -Unknown-
(46297, 396, 0, 0, 0, 0, 0, 0, 0, '现在，轮到主武器了。', 25996), -- -Unknown-
(47589, 0, 0, 0, 0, 0, 0, 0, 0, '我们还有机会。', 25996), -- -Unknown-
(48483, 0, 0, 0, 0, 0, 0, 0, 0, '一个戴着兜帽的人……而且它还没有攻击你？$B$B真奇怪。', 25996), -- -Unknown-
(46938, 396, 0, 0, 0, 0, 0, 0, 0, '欢迎来到阿古斯，燃烧军团的大本营。', 25996), -- -Unknown-
(48440, 603, 0, 0, 0, 0, 0, 0, 0, '我们必须快点行动起来！', 25996), -- -Unknown-
(47224, 1, 0, 0, 0, 0, 0, 0, 0, '这一天，我已经等了很多年。$B$B见证德莱尼的胜利，维迪卡尔！', 25996), -- -Unknown-
(47223, 0, 0, 0, 0, 0, 0, 0, 0, '感谢圣光，你终于来了。', 25996), -- -Unknown-
(47222, 396, 0, 0, 0, 0, 0, 0, 0, '愿圣光能一直保佑我们。', 25996), -- -Unknown-
(47221, 1, 0, 0, 0, 0, 0, 0, 0, '我很高兴你也加入了前往阿古斯的远征。前面会有很多战斗等待着你。', 25996), -- -Unknown-
(40753, 0, 0, 0, 0, 0, 0, 0, 0, '我的功能是发放礼物，根据我的观察显示，你的功能是接收礼物。$b$b我们可以进行一次互惠互利的交易吗？', 25996); -- -Unknown-

