(32638, @GROUP_ID+0, @ID+0, '你为什么要这样对待哈克穆德？', 12, 0, 100, 6, 0, 0, 33015, '阿古斯的哈克穆德 to Player'),
(32639, @GROUP_ID+0, @ID+0, '再见！你曾经是尼莫最好的朋友！', 12, 0, 100, 3, 0, 0, 33020, '尼莫 to Player'),
(61156, @GROUP_ID+0, @ID+0, '跑吧，懦夫！乡巴佬！这片土地将重归自然的怀抱！', 14, 0, 100, 0, 0, 0, 60741, '仇灰'),
(61203, @GROUP_ID+0, @ID+0, '非常感谢……我已经坚持不了多久了。', 12, 0, 100, 2, 0, 0, 60930, '酒仙曲子 to Player'),
