INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `comment`) VALUES
(121754, @GROUP_ID+0, @ID+0, '现在就看部落会不会响应维伦的号召了。那些野蛮人缺乏荣誉感，但是很擅长战斗。', 12, 0, 100, 0, 0, 87088, 130826, '温蕾萨·风行者'),
(121755, @GROUP_ID+0, @ID+0, '我们必须找到圣光军团。在他们的协助下，军团必将覆灭。我们必须找到……', 12, 0, 100, 0, 0, 86878, 130856, '救赎者阿拉托尔'),
(121756, @GROUP_ID+0, @ID+0, '你的父母还活着，阿拉托尔，我肯定。但现在，专心迎接之后的战斗吧。', 12, 0, 100, 0, 0, 87998, 132489, '守备官波鲁斯');
INSERT INTO `creature_text_locale` (`CreatureID`, `GroupID`, `ID`, `locale`, `Text`) VALUES
(121754, @GROUP_ID+0, @ID+0, 'zhCN', '现在就看部落会不会响应维伦的号召了。那些野蛮人缺乏荣誉感，但是很擅长战斗。'),
(121755, @GROUP_ID+0, @ID+0, 'zhCN', '我们必须找到圣光军团。在他们的协助下，军团必将覆灭。我们必须找到……'),
(121756, @GROUP_ID+0, @ID+0, 'zhCN', '你的父母还活着，阿拉托尔，我肯定。但现在，专心迎接之后的战斗吧。');
